CREATE TABLE IF NOT EXISTS ONEHOME_USER(ID INT AUTO_INCREMENT PRIMARY KEY,NAME VARCHAR(255) NOT NULL,EMAIL VARCHAR(255) NOT NULL,MOBILE_NO VARCHAR(200) NOT NULL,ADDRESS_LINE1 VARCHAR(255),ADDRESS_LINE2 VARCHAR(255),STATE VARCHAR(255),CREATED_ON DATE NOT NULL,CREATED_BY VARCHAR(255) NOT NULL,MODIFIED_ON DATE NOT NULL,MODIFIED_BY VARCHAR(255) NOT NULL)